import { useState } from "react";

export default function PostRecipe(props) {
  console.log(props)
  const [formData, setFormData] = useState({
    name: "",
    prep_time: "",
    baking_cooking_time: "",
    servings: "",
    ingredients: "",
    directions: "",
    img_url: "",
  })

  const {
    name,
    prep_time,
    baking_cooking_time,
    servings,
    ingredients,
    directions,
    img_url } = formData;
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <div>
      <h3>Post Page</h3>
      <div className="post-container" onSubmit={(e) => {
        e.preventDefault();
        handleChange(formData);
      }}>
        <form
        className="post-form"
        onSumbit={(e) => {
          e.preventDefault();
          handleUpdate(id, formData);
          }}
        >
          <input className="recipe-name"
          type="text"
          name="name"
          value={name}
          placeholder="Recipe Name:"  
          onChange={handleChange} />
        <input className="prep-time"
          type="text"
          name="prep_time"
          value={prep_time}
          placeholder="Prep Time:"
          onChange={handleChange}
        />
        <input className="bake_cook_time"
          type="text"
          name="baking_cooking_time"
          value={baking_cooking_time}
          placeholder="Baking/Cooking Time:"  
          onChange={handleChange}
        />
        <input
          type="text"
          name="servings"
          value={servings}
          onChange={handleChange}
        />
        <input
          type="text"
          name="ingredients"
          value={ingredients}
          onChange={handleChange}
        />
        <input
          type="text"
          name="directions"
          value={directions}
          onChange={handleChange}
        />
        <input
          type="text"
          name="img_url"
          value={img_url}
          onChange={handleChange}
        />
        <button className="edit-post-button">Submit</button>

        </form>

      </div>
    </div>
  )
}
